import {BUY_CAKE,CREATE_CAKE,DELETE_CAKE} from './cakeType'
const intialState={
       cakes:[
        { id:1,
        name:'chocolet cake',
        type:'fasting',
        numOfCake:10
       },
       { id:2,
        name:'chocolet cake',
        type:'fasting',
        numOfCake:10
       },
    { 
        id:3,
    name:'chocolet cake',
       type:'fasting',
        numOfCake:10
        }
       ],
}

const reducer = (state = intialState,action)=>{
    const {type,payload} = action
       console.log('from reducer ', action.payload)
     switch(type){
        case BUY_CAKE:{
            return{
                ...state,
                cakes:state.cakes.map(item => item.id === payloaparseInt(payload,10) ?  item.numOfCake - 1 : item) 
            }
        }
        case  DELETE_CAKE:{
            debugger
            return {
                ...state,
                cakes:state.cakes.filter(itme => itme.id !== parseInt(payload,10))

            }
        }
        case CREATE_CAKE :{
             return {
                 ...state,
                 cakes:[...state.cakes , action.payload] 
             }
        }
        default:return state
     }
    }
export default reducer
