import React, { Component } from 'react'
import {useSelector , useDispatch} from 'react-redux'
import {getCake} from '../redux'
export default class Cake extends Component {
    state={
       cakes:[]
    }

    componentDidMount=()=>{

        // this.setState({cakes:cakes})
        // const cake = useSelector(state => state.cakes)
        // this.setState({cakes:cake }, console.log('list of cakes' , this.cakes))
    }
    render() {
        const dispatch=useDispatch()
        const cakes = dispatch(()=>getCake())
        return (
            <div>
                <form>
                    <div>
                        <lable>cake name</lable>
                        <input type='text' name='cakeName'/>
                    </div>

                    <div>
                        <lable>cake type</lable>
                        <input type='text' name='type'/>
                    </div>
                    <div>
                        <lable>Number of cake</lable>
                        <input type='text' name='numOfCake'/>
                    </div>
                    <div>
                        <button type='submit'>submit</button>
                    </div>
                </form>
               <ul>
                   {/* {this.state.cakes.map(itme=>(
                       <li>{itme.id},{itme.name},{itme.numOfCake}</li>
                   ))} */}
                   {cakes.map(itme=>(
                       <li>{itme.id},{itme.name},{itme.numOfCake}</li>
                   ))}
               </ul>
            </div>
        )
    }
}
