import React, { Component,useState } from 'react'
import {useSelector, useDispatch} from 'react-redux'

import {getCake,createCake} from '../redux'
export default function Cake(){
        const [cake , setCake]= useState({name:'hello',numOfCake:0,type:'hiiii'})
         const dispatch = useDispatch()
        const cakes = useSelector(state => state.cakes)
       const onChange=(e)=> {
              setCake({[e.target.name]:e.target.value})
        }
       const onSubmitClick =(e) =>{
           e.preventDefault()
           console.log(cake)
        //    dispatch(createCake(cake))
        }
        return (
            <div>
                <form>
                    <div>
                        <lable>cake name</lable>
                        <input type='text' name='name'  onChange={(e)=>setCake({name:e.target.value})}/>
                    </div>

                    <div>
                        <lable>cake type</lable>
                        <input type='text' name='type'  onChange={(e)=>setCake({name:e.target.value})}/>
                    </div>
                    <div>
                        <lable>Number of cake</lable>
                        <input type='text' name='numOfCake'  onChange={(e)=>setCake({name:e.target.value})}/>
                    </div>
                    <div>
                        <button type='submit' onClick={onSubmitClick}>submit</button>
                    </div>
                </form>
               <ul>
                   {/* {this.state.cakes.map(itme=>(
                       <li>{itme.id},{itme.name},{itme.numOfCake}</li>
                   ))} */}
                   {cakes.map(itme=>(
                       <li> id ={itme.id}, cake name = {itme.name},num of cake{itme.numOfCake}</li>
                   ))}
               </ul>
            </div>
        )

}
